package com.freedomoss.requester;

# list any import classes here.
import java.util.Map;
import java.util.ArrayList;
import com.freedomoss.objective.model.CompositeRuleContext;
import com.freedomoss.objective.model.CompositeRuleDataItemContext;
import org.slf4j.Logger;


# declare any global variables here
global CompositeRuleContext source
global Logger log
global Map params


rule "Rule context initialization"
auto-focus true
no-loop
agenda-group "initialization-group"
    when
        $ctx:CompositeRuleContext(initialized == false);
    then
        # insert processed facts into memory
        $ctx.updateWorkingMemory();
        # move to business rules
        kcontext.getKnowledgeRuntime().getAgenda().getAgendaGroup("calculation").setFocus();
end

# RuleOutcome.Definition={"outcomeId":"TOOD","conditional":true,"reworkBySameWorker":false,"assignToSameWorker":false,"anableLogging":false,"sendRejectNotification":false,"assignToAnyWorker":false,"approveRelatedAssignments":false,"rejectRelatedAssignments":false,"renameAutomationAnswerItem":false,"conditions":[{"fieldName":"ocr_type","value":"tood","operator":"EQ"}],"customConditions":[]}
rule "1. Send records to 'TOOD' when ocr_type EQ 'tood'"
agenda-group "calculation"
dialect "mvel"
salience 100
    when
        $ctx:CompositeRuleContext(initialized == true);
        $item:CompositeRuleDataItemContext();
        eval($item.checkValue("ocr_type", "EQ", "tood"));

    then
        $ctx.sendResultTo("TOOD", $item);
end

# RuleOutcome.Definition={"outcomeId":"Standard","conditional":true,"reworkBySameWorker":false,"assignToSameWorker":false,"anableLogging":false,"sendRejectNotification":false,"assignToAnyWorker":false,"approveRelatedAssignments":false,"rejectRelatedAssignments":false,"renameAutomationAnswerItem":false,"conditions":[{"fieldName":"ocr_type","value":"standard","operator":"EQ"}],"customConditions":[]}
rule "2. Send records to 'Standard' when ocr_type EQ 'standard'"
agenda-group "calculation"
dialect "mvel"
salience 100
    when
        $ctx:CompositeRuleContext(initialized == true);
        $item:CompositeRuleDataItemContext();
        eval($item.checkValue("ocr_type", "EQ", "standard"));

    then
        $ctx.sendResultTo("Standard", $item);
end



rule "Send unprocessed result to unconditional outcome(s)"
agenda-group "calculation"
dialect "mvel"
salience 80
    when
        $ctx:CompositeRuleContext(initialized == true);
        $item:CompositeRuleDataItemContext();
    then
        $ctx.sendResultToUnconditionalOutcomes($item, true);
end

package com.freedomoss.requester;
#list any import classes here. import com.freedomoss.objective.model.RuleContext; import com.freedomoss.objective.model.RuleContext.MajorityType; import com.freedomoss.objective.model.RuleAssigmentContext; import com.freedomoss.objective.model.RuleQuestionContext; import com.freedomoss.requester.model.AwsHitQuestion; import com.freedomoss.requester.model.AwsHitQuestionItem; import com.freedomoss.requester.model.AwsHitQuestionAnswerItem;
import org.slf4j.Logger; import java.util.ArrayList; import java.util.List; import java.util.Set; import java.util.Map; import java.util.Iterator;
#declare any global variables here global RuleContext source global Logger log global Map params
rule "Rule context initialization" auto-focus true no-loop dialect "mvel" agenda-group "initialization-group" when $ctx:RuleContext(initialized == false); then # set parameters
$ctx.properties[RuleContext.MAJORITY_TYPE] = RuleContext.MajorityType.COUNT; $ctx.properties[RuleContext.MAJORITY_VALUE] = new Integer(1);
#$ctx.properties[RuleContext.MAX_ASSIGNMENT_LIMIT] = new Integer(1); $ctx.properties[RuleContext.MAJORITY_HIT_THRESHOLD] = new Double(100/100); $ctx.properties[RuleContext.ASSIGNMENT_APPROVE_THRESHOLD] = new Double(0.5);
# insert processed facts into memory $ctx.updateWorkingMemory(); # move to business rules kcontext.getKnowledgeRuntime().getAgenda().getAgendaGroup("calculation").setFocus(); $ctx.logExecutedRule(kcontext.getRule().getName()); end
rule "Worst accuracy rule-Evaluate every 5 gold question and set Accuracy based qualification score to 70 if gold accuracy goes up < 75 percents" agenda-group "calculation" dialect "mvel" salience 250 no-loop when $ctx:RuleContext(initialized == true) $rac:RuleAssigmentContext($campaignStatistic:campaignStatistic, runStatistic.totalGoldQuestions > 0) #evaluate run average response time eval($campaignStatistic.totalGoldQuestions > 0 && ($campaignStatistic.totalGoldQuestions % 5) == 0 && $campaignStatistic.goldAccuracy < (75/100)) then $rac.grandQualification(RuleContext.ACCURACY_BASED_QUALIFICATION, 70); $ctx.logExecutedRule(kcontext.getRule().getName()); end
rule "Normal accuracy rule-Evaluate every 5 gold question and set Accuracy based qualification score to 80 if gold accuracy goes up >= 75 percents and < 90" agenda-group "calculation" dialect "mvel" salience 250 no-loop when $ctx:RuleContext(initialized == true) $rac:RuleAssigmentContext($campaignStatistic:campaignStatistic, runStatistic.totalGoldQuestions > 0) #evaluate run average response time eval($campaignStatistic.totalGoldQuestions > 0 && ($campaignStatistic.totalGoldQuestions % 5) == 0 && $campaignStatistic.goldAccuracy >= (75/ 100) && $campaignStatistic.goldAccuracy < (90 / 100)) then $rac.grandQualification(RuleContext.ACCURACY_BASED_QUALIFICATION, 80); $ctx.logExecutedRule(kcontext.getRule().getName()); end
rule "Super accuracy rule-Evaluate every 5 gold question and set Accuracy based qualification score to 95 if gold accuracy goes up >= 90 percents" agenda-group "calculation" dialect "mvel" salience 250 no-loop when $ctx:RuleContext(initialized == true) $rac:RuleAssigmentContext($campaignStatistic:campaignStatistic, runStatistic.totalGoldQuestions > 0) #evaluate run average response time eval($campaignStatistic.totalGoldQuestions > 0 && ($campaignStatistic.totalGoldQuestions % 5) == 0 && $campaignStatistic.goldAccuracy >= (90 / 100) ) then $rac.grandQualification(RuleContext.ACCURACY_BASED_QUALIFICATION, 95); $ctx.logExecutedRule(kcontext.getRule().getName()); end
rule "0. Check, if exist majority" agenda-group "calculation" dialect "mvel" salience 100 no-loop when $ctx:RuleContext (initialized == true, $gold:gold, $threshold:properties.MAJORITY_HIT_THRESHOLD) eval($ctx.majorityWithoutGold().size() >= ($ctx.questions.size() - $gold.size()) * $threshold) then insert(new String("MAJORITY_FOUND")); $ctx.logExecutedRule(kcontext.getRule().getName()); end
rule "1. Approve question by majority" agenda-group "calculation" dialect "mvel" salience 90 no-loop when ((String(toString == "MAJORITY_FOUND") and $ctx:RuleContext (initialized == true)) or (not String(toString == "MAJORITY_FOUND") and $ctx:RuleContext (initialized == true, assignments.size == properties.MAX_ASSIGNMENT_LIMIT))) $rqc:RuleQuestionContext() then $rqc.approveQuestion($ctx.majority()); $ctx.logExecutedRule(kcontext.getRule().getName(), $rqc); end
rule "3. Approve assignment (always)" agenda-group "calculation" dialect "mvel" salience 50 no-loop when $ctx:RuleContext(initialized == true, $threshold:properties.ASSIGNMENT_APPROVE_THRESHOLD) $rac:RuleAssigmentContext() then insert(new String("ASSIGNMENT_PROCESSED")); $ctx.addApproved($rac); $ctx.logExecutedRule(kcontext.getRule().getName(), $rac); end
rule "5. Extend HIT" agenda-group "calculation" dialect "mvel" salience 50 no-loop when not String(toString == "MAJORITY_FOUND") $ctx:RuleContext(initialized == true, assignments.size < properties.MAX_ASSIGNMENT_LIMIT); then $ctx.setExtendHit(true); $ctx.logExecutedRule(kcontext.getRule().getName()); end

