rule "Worst accuracy rule-Evaluate every 5 gold question and set Accuracy based qualification score to 70 if gold accuracy goes up < 75 percents" agenda-group "calculation" dialect "mvel" salience 250 no-loop when $ctx:RuleContext(initialized == true) $rac:RuleAssigmentContext($campaignStatistic:campaignStatistic, runStatistic.totalGoldQuestions > 0) #evaluate run average response time eval($campaignStatistic.totalGoldQuestions > 0 && ($campaignStatistic.totalGoldQuestions % 5) == 0 && $campaignStatistic.goldAccuracy < (75/100)) then $rac.grandQualification(RuleContext.ACCURACY_BASED_QUALIFICATION, 70); $ctx.logExecutedRule(kcontext.getRule().getName()); end
rule "Normal accuracy rule-Evaluate every 5 gold question and set Accuracy based qualification score to 80 if gold accuracy goes up >= 75 percents and < 90" agenda-group "calculation" dialect "mvel" salience 250 no-loop when $ctx:RuleContext(initialized == true) $rac:RuleAssigmentContext($campaignStatistic:campaignStatistic, runStatistic.totalGoldQuestions > 0) #evaluate run average response time eval($campaignStatistic.totalGoldQuestions > 0 && ($campaignStatistic.totalGoldQuestions % 5) == 0 && $campaignStatistic.goldAccuracy >= (75/ 100) && $campaignStatistic.goldAccuracy < (90 / 100)) then $rac.grandQualification(RuleContext.ACCURACY_BASED_QUALIFICATION, 80); $ctx.logExecutedRule(kcontext.getRule().getName()); end
rule "Super accuracy rule-Evaluate every 5 gold question and set Accuracy based qualification score to 95 if gold accuracy goes up >= 90 percents" agenda-group "calculation" dialect "mvel" salience 250 no-loop when $ctx:RuleContext(initialized == true) $rac:RuleAssigmentContext($campaignStatistic:campaignStatistic, runStatistic.totalGoldQuestions > 0) #evaluate run average response time eval($campaignStatistic.totalGoldQuestions > 0 && ($campaignStatistic.totalGoldQuestions % 5) == 0 && $campaignStatistic.goldAccuracy >= (90 / 100) ) then $rac.grandQualification(RuleContext.ACCURACY_BASED_QUALIFICATION, 95); $ctx.logExecutedRule(kcontext.getRule().getName()); end
rule "0. Check, if exist majority" agenda-group "calculation" dialect "mvel" salience 100 no-loop when $ctx:RuleContext (initialized == true, $gold:gold, $threshold:properties.MAJORITY_HIT_THRESHOLD) eval($ctx.majorityWithoutGold().size() >= ($ctx.questions.size() - $gold.size()) * $threshold) then insert(new String("MAJORITY_FOUND")); $ctx.logExecutedRule(kcontext.getRule().getName()); end
rule "1. Approve question by majority" agenda-group "calculation" dialect "mvel" salience 90 no-loop when ((String(toString == "MAJORITY_FOUND") and $ctx:RuleContext (initialized == true)) or (not String(toString == "MAJORITY_FOUND") and $ctx:RuleContext (initialized == true, assignments.size == properties.MAX_ASSIGNMENT_LIMIT))) $rqc:RuleQuestionContext() then $rqc.approveQuestion($ctx.majority()); $ctx.logExecutedRule(kcontext.getRule().getName(), $rqc); end
rule "3. Approve assignment (always)" agenda-group "calculation" dialect "mvel" salience 50 no-loop when $ctx:RuleContext(initialized == true, $threshold:properties.ASSIGNMENT_APPROVE_THRESHOLD) $rac:RuleAssigmentContext() then insert(new String("ASSIGNMENT_PROCESSED")); $ctx.addApproved($rac); $ctx.logExecutedRule(kcontext.getRule().getName(), $rac); end
rule "5. Extend HIT" agenda-group "calculation" dialect "mvel" salience 50 no-loop when not String(toString == "MAJORITY_FOUND") $ctx:RuleContext(initialized == true, assignments.size < properties.MAX_ASSIGNMENT_LIMIT); then $ctx.setExtendHit(true); $ctx.logExecutedRule(kcontext.getRule().getName()); end

